INSERT INTO ProductosCafeteria (ID, Nombre, Categoria, ImagenRuta, Descripcion, PrecioEuros) VALUES
(1, 'Amanecer Americano', 'Bebida', '../imagenes/cafeteria/cafe1.png', 'El clásico que nunca falla. Suave, aromático y ligero, ideal para comenzar el día.', 2.50),
(2, 'Niebla Matutina (Latte)', 'Bebida', '../imagenes/cafeteria/cafe2.png', 'La combinación perfecta de intensidad y suavidad. Un trago de café y espuma sedosa.', 3.00),
(3, 'El Trío de Milán (Cappuccino)', 'Bebida', '../imagenes/cafeteria/cafe3.png', 'La tradicional bebida italiana, equilibrada en sabor y coronada con una densa capa de espuma.', 3.20),
(4, 'Corazón Negro (Espresso)', 'Bebida', '../imagenes/cafeteria/cafe4.png', 'La esencia pura. Corto, intenso y con una crema perfecta. Solo para los más valientes.', 2.00),
(5, 'Armonía Perfecta (Café con Leche)', 'Bebida', '../imagenes/cafeteria/cafe5.png', 'Un café con leche cremoso y reconfortante. El equilibrio entre el café y la leche.', 2.80),
(6, 'Toque de Luz (Macchiato)', 'Bebida', '../imagenes/cafeteria/cafe6.png', 'Un espresso con solo un toque de leche para suavizar la intensidad. Fuerte y elegante.', 2.70),
(7, 'Origen Etiopía Yirgacheffe (Filtrado)', 'Bebida', '../imagenes/cafeteria/cafe7.png', 'Café de cuerpo ligero con vibrantes notas afrutadas de jazmín y cítricos. Preparado en filtro.', 4.50),
(8, 'Colombia Supremo (Chemex)', 'Bebida', '../imagenes/cafeteria/cafe8.png', 'Un café limpio y equilibrado con notas a caramelo y nuez. Preparación elegante y precisa en Chemex.', 4.80),
(9, 'Capricho de Kenia (V60)', 'Bebida', '../imagenes/cafeteria/cafe9.png', 'Intenso y complejo, con acidez brillante y toques de grosella negra y vino. Servido en método V60.', 4.70),
(10, 'Gota de Brasil (Flat White)', 'Bebida', '../imagenes/cafeteria/cafe10.png', 'Un espresso intenso de especialidad brasileño con un sabor a chocolate y nuez, coronado con una fina capa de leche cremosa.', 3.90),
(11, 'Elixir de Sumatra (Cold Brew)', 'Bebida', '../imagenes/cafeteria/cafe11.png', 'Extracción en frío de 18 horas para un café de baja acidez, con notas terrosas y achocolatadas. Servido con hielo.', 4.20),
(12, 'Guatemala Antigua (Cortado)', 'Bebida', '../imagenes/cafeteria/cafe12.png', 'Café con cuerpo, dulzor de chocolate y especias. Un espresso cortado con la proporción justa de leche.', 3.60),
(13, 'Pecado Fundido (Chocolate Clásico)', 'Bebida', '../imagenes/cafeteria/chocoTaza.png', 'Nuestro chocolate caliente espeso y tradicional, perfecto para los días fríos.', 3.50),
(14, 'Nube de Invierno (Chocolate con Nata)', 'Bebida', '../imagenes/cafeteria/chocoTaza2.png', 'El clásico Pecado Fundido coronado con un generoso copete de nata montada.', 4.00),
(20, 'Elixir Zen (Matcha Latte)', 'Bebida', '../imagenes/cafeteria/matcha1.png', 'Una bebida energizante sin el nerviosismo del café, con el sabor herbal del té verde japonés.', 3.80),
(21, 'Jardín Japonés (Matcha con Leche)', 'Bebida', '../imagenes/cafeteria/matcha2.png', 'Matcha suavemente batido con leche. Un placer verde y saludable.', 3.50);
INSERT INTO ProductosCafeteria (ID, Nombre, Categoria, ImagenRuta, Descripcion, PrecioEuros) VALUES
(15, 'El Viajero de París (Croissant)', 'Comida', '../imagenes/cafeteria/cruasan.png', 'Crujiente por fuera, tierno por dentro. Elaborado con mantequilla de alta calidad.', 1.80),
(16, 'Susurros de Mantequilla (Minicroissants)', 'Comida', '../imagenes/cafeteria/cruasanesBlancos.png', 'Un trío de pequeños croissants naturales, ideales para picar o acompañar.', 2.50),
(17, 'Tesoro Escondido (Minicroissants de Crema)', 'Comida', '../imagenes/cafeteria/cruasanesCrema.png', 'Mini croissants rellenos de nuestra suave y dulce crema pastelera de vainilla.', 3.00),
(18, 'Tentación Oscura (Minicroissants de Chocolate)', 'Comida', '../imagenes/cafeteria/cruasanesNegros.png', 'Mini croissants con un intenso y fundente relleno de chocolate negro.', 3.00),
(19, 'Lágrimas de Chocolate (Galleta)', 'Comida', '../imagenes/cafeteria/galletas.png', 'Galleta estilo ''cookie'' con grandes trozos de chocolate. Crujiente en los bordes y tierna por dentro.', 2.20),
(22, 'Espiral de Ceylán (Rollito de Canela)', 'Comida', '../imagenes/cafeteria/rollitoCanela.png', 'Suave, esponjoso y con el toque especiado de la canela. Un dulce irresistible.', 2.80),
(23, 'Trilogía de Cacao (Tarta Tres Chocolates)', 'Comida', '../imagenes/cafeteria/tarta3Chocos.png', 'Tres capas de placer: blanco, con leche y negro. Para los verdaderos amantes del chocolate.', 4.50),
(24, 'Volcán de Frutas Prohibidas', 'Comida', '../imagenes/cafeteria/tarta3Chocos2.png', 'Intenso bizcocho de chocolate bañado y cubierto con una cascada de frutos rojos.', 4.80),
(25, 'Suspiro de Nata (Tarta de Queso)', 'Comida', '../imagenes/cafeteria/tartaBlanca.png', 'Nuestra tarta de queso horneada, suave y cremosa, con un sabor ligeramente ácido.', 4.00),
(26, 'El Jardín de las Fresas', 'Comida', '../imagenes/cafeteria/tartaFresas.png', 'Fina tarta de bizcocho, nata aireada y las mejores fresas de temporada.', 4.20),
(27, 'El Otoño de Eva (Tarta de Manzana)', 'Comida', '../imagenes/cafeteria/tartaManzana.png', 'El postre tradicional con manzanas caramelizadas y un toque de canela. Jugosa y tibia.', 3.80),
(28, 'Boscaje Encantado', 'Comida', '../imagenes/cafeteria/tartaNegro.png', 'Una tarta fría y ligera, con el sabor ácido de las bayas silvestres.', 4.00),
(29, 'Volcán de Queso y Bayas', 'Comida', '../imagenes/cafeteria/tartaQueso.png', 'Nuestra Tarta de Queso (Suspiro de Nata) cubierta con una explosión de sabor a frutos rojos.', 4.50),
(30, 'La Tierra de Oz (Tarta de Zanahoria)', 'Comida', '../imagenes/cafeteria/tartaZanahorias.png', 'Húmeda, especiada y con el toque crujiente de las nueces. Cubierta con un glaseado de queso crema.', 4.20),
(31, 'Verde Despertar (Tostada de Aguacate)', 'Comida', '../imagenes/cafeteria/tostadaAguacate.png', 'Una opción saludable y fresca. Pan crujiente con aguacate, aceite de oliva virgen extra y tomate.', 5.00),
(32, 'El Tesoro Ibérico', 'Comida', '../imagenes/cafeteria/tostadaJamon1.png', 'El lujo del desayuno español. Jamón ibérico de la mejor calidad sobre pan y tomate.', 6.50),
(33, 'El Clásico Serrano', 'Comida', '../imagenes/cafeteria/tostadaJamon2.png', 'La versión sencilla y deliciosa, con jamón serrano y el toque esencial del tomate.', 5.80);
