INSERT INTO ProductosFloristeria (ID, Nombre, ImagenRuta, Detalle, DescripcionCuidados, PrecioEuros) VALUES
(500, 'Amapolas', '../imagenes/flores/amapolas.png', 'Un vibrante ramo de amapolas que irradia energía y pasión, perfecto para celebrar la alegría.', 'Las amapolas cortadas son sensibles. Corta los tallos en ángulo y sumérgelos inmediatamente en agua caliente por unos segundos antes de colocarlos en agua fresca. Son originarias de regiones de clima templado de Eurasia y Norteamérica.', 25.00),
(501, 'Camelias', '../imagenes/flores/camelias.png', 'Delicadas camelias en tonos pastel, símbolo de admiración y sofisticación. Un regalo de pura elegancia.', 'La Camelia es originaria de Asia Oriental (China, Japón). Requiere un suelo ácido, bien drenado y protección del sol directo fuerte para evitar que sus hojas se quemen.', 32.00),
(502, 'Campanillas', '../imagenes/flores/campanillas.png', 'Un fresco bouquet de campanillas silvestres, evocando la tranquilidad de los bosques y la frescura de la mañana.', 'Las campanillas (Campanula spp.) prefieren la sombra parcial o plena y suelos húmedos, pero bien drenados. Muchas especies Tesoson nativas de las regiones templadas del hemisferio norte.', 20.00),
(503, 'Claveles', '../imagenes/flores/claveles.png', 'Claveles en tonos vivos, un clásico lleno de encanto que expresa amor puro y fascinación.', 'Para alargar la vida del clavel, cambia el agua diariamente y haz un corte diagonal en los tallos cada dos días. El clavel común (Dianthus caryophyllus) es nativo de la región mediterránea.', 18.00),
(504, 'Claveles Pastel', '../imagenes/flores/claveles2.png', 'Claveles suaves en tonalidades pastel, transmitiendo dulzura y afecto sincero. Ideal para un gesto delicado.', 'Cambiar el agua y recortar los tallos de los claveles con frecuencia es crucial. Son flores robustas originarias del Mediterráneo y cultivadas globalmente.', 19.00),
(505, 'Ramo Colorado', '../imagenes/flores/colorado.png', 'Un ramo silvestre con una explosión de colores cálidos que recuerdan los campos al atardecer.', 'Este tipo de arreglos silvestres deben mantenerse en un lugar fresco, lejos de la luz solar directa y fuentes de calor. Requieren agua limpia y fresca con nutrientes para ramos.', 28.00),
(506, 'Corazones Sangrantes', '../imagenes/flores/corazones-sangrientes.png', 'Exóticas flores en forma de corazón, un regalo único para expresar un amor profundo y misterioso.', 'El Corazón Sangrante (Dicentra spectabilis) es originario de Asia, específicamente de Siberia, Corea, China y Japón. Prefiere la sombra o sombra parcial y suelo uniformemente húmedo.', 35.00),
(507, 'Crocus', '../imagenes/flores/crocus.png', 'Un bouquet de crocus en tonos lilas, anunciando la llegada de la primavera y nuevos comienzos.', 'Los Crocus son plantas bulbosas de fácil cuidado, originarias de Asia Menor y del sur de Europa. Necesitan pleno sol o sombra parcial y suelos bien drenados.', 22.00),
(508, 'Dalias', '../imagenes/flores/dalias.png', 'Dalias en tonos cálidos y anaranjados, ofreciendo una explosión de color y alegría para cualquier ocasión.', 'Las Dalias (Dahlia) son nativas de México y Centroamérica. En jarrones, requieren cambiar el agua a diario y mantenerlas lejos de la fruta, ya que el etileno acelera su marchitamiento.', 30.00),
(509, 'Flor de Loto', '../imagenes/flores/florDeLoto.png', 'Delicadas flores que evocan la paz y la pureza de la flor de loto. Un regalo que inspira calma.', 'La Flor de Loto (Nelumbo nucifera) es nativa de Asia y Australia. Para su cuidado, el agua en la que se coloca debe estar limpia y debe protegerse de la luz solar directa intensa.', 38.00),
(510, 'Girasoles', '../imagenes/flores/girasoles.png', 'Girasoles majestuosos que irradian felicidad y optimismo. Un pedacito de sol para alegrar el día.', 'Los Girasoles (Helianthus annuus) son originarios de Norteamérica. Necesitan mucha agua; revisa el nivel dos veces al día, ya que sus grandes cabezas evaporan el agua rápidamente.', 29.00),
(511, 'Hortensias', '../imagenes/flores/hortensias.png', 'Un voluminoso ramo de hortensias azules, que aportan un toque de frescura y romanticismo campestre.', 'Las Hortensias son originarias de Asia y América. Si la cabeza de la flor se marchita, sumerge toda la cabeza en un baño de agua fresca por 20 minutos para rehidratarla.', 34.00),
(512, 'Latanas', '../imagenes/flores/latanas.png', 'Un colorido arreglo de latanas que aporta un toque tropical y lleno de vitalidad a cualquier espacio.', 'Las Lantanas son nativas de las regiones tropicales de América. Son flores resistentes y con poco mantenimiento que prosperan en pleno sol y resisten bien la sequía.', 23.00),
(513, 'Lirios Blancos', '../imagenes/flores/lirios.png', 'Elegantes lirios blancos, símbolos de pureza e inocencia. Un ramo que irradia sofisticación.', 'Los Lirios (Lilium) son nativos del hemisferio norte templado. Retira el polen de las anteras tan pronto como se abran para evitar manchas en los pétalos y prolongar su vida.', 37.00),
(514, 'Margaritas', '../imagenes/flores/margaritas.png', 'Un clásico bouquet de margaritas blancas y amarillas, lleno de alegría y sencillez. Perfecto para cualquier ocasión.', 'Las Margaritas (Leucanthemum vulgare) son nativas de Europa y Asia templada. Mantén sus tallos en agua limpia con frecuencia y quita las hojas que queden sumergidas.', 17.00),
(515, 'Nardo Azul', '../imagenes/flores/nardoAzul.png', 'Nardos en un cautivador tono azul, una flor elegante y con un aroma sutil. Exclusividad en cada pétalo.', 'El Nardo (Polianthes tuberosa) es nativo de México. Para flores cortadas, colócalas en un lugar fresco y cambia el agua cada dos días para mantener la calidad de su fragancia.', 40.00),
(516, 'Orquídeas Blancas', '../imagenes/flores/orquideas.png', 'Exóticas orquídeas blancas, un símbolo de belleza y lujo. Un regalo impresionante y duradero.', 'Las Orquídeas Phalaenopsis (las más comunes) son originarias del sudeste asiático. Requieren luz indirecta brillante, alta humedad y riego solo cuando el sustrato está casi seco.', 50.00),
(517, 'Orquídeas Mixtas', '../imagenes/flores/orquideas2.png', 'Un vibrante arreglo de orquídeas de varios colores, creando una armonía exótica y sofisticada.', 'Las orquídeas prosperan en luz brillante filtrada (no sol directo) y detestan el exceso de riego. Su hábitat natural suele ser tropical, creciendo sobre árboles (epífitas).', 52.00),
(518, 'Paniculata Blanca', '../imagenes/flores/paniculata.png', 'Delicados ramilletes de paniculata, conocidos como ''velo de novia''. Pura ternura y romanticismo.', 'La Paniculata o Gypsophila es originaria de Eurasia, África y Australia. Para usarla como flor seca, cuélgala boca abajo en un lugar oscuro y seco hasta que el tallo esté rígido.', 15.00),
(519, 'Paniculata Lila', '../imagenes/flores/paniculata2.png', 'Paniculata teñida en suaves tonos lilas, aportando un toque de fantasía y delicadeza.', 'Esta flor es muy popular como ''relleno'' en ramos. Al ser teñida, evita que el agua salpique fuera del jarrón, ya que el tinte podría manchar algunas superficies.', 16.00),
(520, 'Paniculata Multicolor', '../imagenes/flores/paniculata3.png', 'Paniculata en una explosión de colores vibrantes, ideal para celebraciones y momentos alegres.', 'Para mantener su color vibrante, evita exponerla directamente al sol si está teñida. Es una flor de larga duración tanto en fresco como en seco.', 18.00),
(521, 'Peonías', '../imagenes/flores/peonias.png', 'Lujosas peonías en plena floración, un símbolo de prosperidad y amor. Un ramo espectacular.', 'Las Peonías son nativas de Asia, Europa y Norteamérica. Si los capullos no abren, puedes darles un corte fresco al tallo y colocarlos en agua tibia para estimular la apertura.', 45.00),
(522, 'Ramo Fuego y Sol', '../imagenes/flores/ramo1.png', 'Un vibrante ramo que combina la pasión del rojo con la energía del amarillo y naranja, creando un efecto de calidez.', 'Para ramos mixtos, retira las flores marchitas inmediatamente para evitar que liberen gases que acorten la vida del resto de las flores. Mantén el agua con nutrientes para flores.', 30.00),
(523, 'Ramo Sabor a Primavera', '../imagenes/flores/ramo2.png', 'Fresco y colorido, este ramo celebra la vitalidad de la primavera con una mezcla armoniosa de flores.', 'Cambia el agua del jarrón cada dos días, asegurándote de que el agua esté a temperatura ambiente. La mayoría de estas flores provienen de cultivos de invernadero para asegurar su frescura.', 33.00),
(524, 'Ramo Campo de Colores', '../imagenes/flores/ramo3.png', 'Una selección variada de flores que emulan la belleza de un campo en plena floración, lleno de vida y alegría.', 'Mantén el ramo lejos de corrientes de aire y fuentes de calor. El aire seco y el calor excesivo son los principales enemigos de la longevidad de las flores cortadas.', 31.00),
(525, 'Rosas Blancas', '../imagenes/flores/rosasBlancas.png', 'Elegantes rosas blancas, símbolo de inocencia, pureza y nuevos comienzos. Un clásico atemporal.', 'Las Rosas son nativas de Asia. Para su cuidado, elimina las hojas que queden bajo el nivel del agua para prevenir bacterias y siempre haz un corte diagonal en el tallo.', 28.00),
(526, 'Rosas Negras', '../imagenes/flores/rosasNegras.png', 'Rosas negras, exóticas y enigmáticas. Un regalo único que evoca misterio y elegancia sofisticada.', 'La rosa negra es, en realidad, una rosa roja o granate muy oscura lograda mediante hibridación o teñido. Sigue los mismos cuidados básicos que las rosas tradicionales.', 38.00),
(527, 'Rosas Rojas', '../imagenes/flores/rosasRojas.png', 'El clásico ramo de rosas rojas, el símbolo definitivo del amor profundo y la pasión ardiente.', 'La rosa es una de las flores más cultivadas en el mundo, con centros de producción importantes en Ecuador y Kenia. Usa el sobre de nutrientes florales en el agua para un mayor disfrute.', 35.00),
(528, 'Flor de Cerezo (Sakuras)', '../imagenes/flores/sakuras.png', 'Delicadas ramas de flor de cerezo, capturando la efímera belleza de la primavera japonesa.', 'La flor de cerezo (Sakura) es originaria de Asia Oriental, siendo Japón el país más famoso. Para ramas cortadas, golpea suavemente la base del tallo para ayudar a que absorba agua.', 27.00),
(529, 'Tulipanes', '../imagenes/flores/tulipanes.png', 'Un ramo vibrante de tulipanes en tonos rojos y naranjas, que simboliza la alegría y el amor perfecto.', 'Los Tulipanes son originarios de Asia Central y Turquía. Siguen creciendo en el jarrón; colócalos en poca agua y en un lugar fresco para controlar su crecimiento y que se mantengan erguidos.', 26.00),
(530, 'Violetas', '../imagenes/flores/violetas.png', 'Pequeñas y encantadoras violetas en tonos lavanda, perfectas para un detalle dulce y nostálgico.', 'Las violetas (Viola odorata) son nativas de Europa y Asia. Las violetas en maceta prefieren la sombra parcial y un suelo constantemente húmedo pero bien drenado.', 19.00);
